import "./App.css";
import { useState, useEffect } from "react";
import Title from "./components/Title";
import Library from "./components/Library";
import * as BooksAPI from "./ContactsAPI";


function App() {

  //function for changing book's shelf by using the action menu
  function bookNewShelf(clickedBook, newShelf) {
    const upToDateLibrary = books.map((eachBook) => {
      if (eachBook.id === clickedBook.id) {
        //set the new shelf value to be the book's new shelf
        clickedBook.currentShelf = newShelf
        return clickedBook;     
      }
      else return eachBook;
      
    }) 
    setBooks(upToDateLibrary);
  }

  const [showSearchPage, setShowSearchpage] = useState(false);

  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    };

    getBooks();
  }, [])

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <Title />
          <div className="list-books-content">
            <div>
              <Library books={books} bookNewShelf={bookNewShelf} />
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
