import React from "react";


function Book({book, bookNewShelf}) {

    function handleChange(event) {
        //handling the option the user choose and for which function
        //bookNewShelf is a function in app.js file and passed doen using props to this file
       return bookNewShelf(book, event.target.value);
    
    }

    let noShelf;

    if(book.shelf) {
        return noShelf= book.shelf;
    }else {
        return noShelf= "none";
    }
    
    return <li>
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 193,
            backgroundImage:
              `url(${book.imageLinks.thumbnail})`
          }}
        ></div>
        <div className="book-shelf-changer">
          <select defaultValue={book.shelf ? book.shelf : "none"} onChange={handleChange}>
          
            <option value="none" disabled>
              Move to...
            </option>
            <option value="currentlyReading">
            Currently Reading
            </option>
            <option value="wantToRead">Want to Read</option>
            <option value="read">Read</option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{book.title}</div>
      <div className="book-authors">{book.authors}</div>
    </div>
  </li>
  
}



export default Book;
