import React from "react";
import Select from "react-select";


function Book({book, bookNewShelf}) {

    function handleChange(event) {
        //handling the option the user choose and for which function
        //bookNewShelf is a function in app.js file and passed doen using props to this file
       return bookNewShelf(book, event.target.value);
    
    }
    
    return <li>
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 193,
            backgroundImage:
              `url(${book.bookImageURL})`
          }}
        ></div>
        <div className="book-shelf-changer">
          <select defaultValue={book.currentShelf} onChange={handleChange}>
          
            <option value="none" disabled>
              Move to...
            </option>
            <option value="Currently Reading">
            Currently Reading
            </option>
            <option value="Want to Read">Want to Read</option>
            <option value="Read">Read</option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{book.bookTitle}</div>
      <div className="book-authors">{book.bookAuthor}</div>
    </div>
  </li>
  
}



export default Book;
